        return (
            <React.Fragment>
                <Link href="/sessions">
                    <a>Sessions</a>
                </Link>

                <ul>
                    {
                    this.state.speakerData.map(
                        (speaker) => {
                            return (
                                <li key={speaker.id}>
                                {speaker.firstName} {speaker.lastName}
